import{I as e}from"./chunks/Items.BoxPNSHn.js";import{c as t,a7 as r,G as o,k as l,o as d}from"./chunks/framework.B3DYf-j4.js";const s=[],u=JSON.parse('{"title":"癞蛤蟆先生去看心理医生","description":"","frontmatter":{},"headers":[],"relativePath":"literature/excerpt-toad.md","filePath":"literature/excerpt-toad.md","lastUpdated":1737295895000}'),n={name:"literature/excerpt-toad.md"},m=Object.assign(n,{setup(i){return(p,a)=>(d(),t("div",null,[a[0]||(a[0]=r('<h1 id="癞蛤蟆先生去看心理医生" tabindex="-1">癞蛤蟆先生去看心理医生 <a class="header-anchor" href="#癞蛤蟆先生去看心理医生" aria-label="Permalink to &quot;癞蛤蟆先生去看心理医生&quot;">​</a></h1><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>蛤蟆先生一向爱笑爱闹，如今却一反常态地郁郁寡欢，他一个人躲在屋里，连起床梳洗的力气都没有。朋友们非常担心他，建议他去做心理咨询。在 10 次心理咨询中，蛤蟆在咨询师苍鹭的带领下，勇敢地探索了自己的内心世界，也逐渐找回了信心与希望……</p><p>这并不是一本写给孩子看的书，而是一本非常有深度的心理疗愈读物。出版 20 多年来，已成为英国国民级心理咨询入门书。它基于 TA 沟通分析心理学的理论，讲述了一个抑郁症病人通过十次心理咨询，终于找回快乐和自信的全过程。在书中，可以看到心理咨询的倾听、共情、沟通技巧，也可以看到一个标准的心理咨询流程的模板。读者犹如亲临现场，体验心理咨询的每一个细节，见证疗愈和改变的发生。</p><p>在这本书里，作者借由蛤蟆和心理咨询师苍鹭的互动，探索了蛤蟆自卑、软弱、爱炫耀的个性与抑郁的情绪究竟来源于何处，让读者看到童年经历对人格的深刻影响，以及如何才能在心理上真正长大成人，独立、自信、充满希望地生活。</p><p>豆瓣评分：8.3 ⭐⭐⭐⭐</p><p>在线阅读：<a href="https://weread.qq.com/web/reader/66832530721e777066806c9" target="_blank" rel="noreferrer">微信读书</a></p><h2 id="摘抄" tabindex="-1">摘抄 <a class="header-anchor" href="#摘抄" aria-label="Permalink to &quot;摘抄&quot;">​</a></h2>',8)),o(e,{data:[...l(s)]},null,8,["data"])]))}});export{u as __pageData,m as default};
