import{I as r}from"./chunks/Items.Ci2XLtuR.js";import{d as l,l as s,c,b as i,g as e,k as n,j as a}from"./chunks/framework.CUewYLlF.js";const x=JSON.parse('{"title":"人性的弱点","description":"","frontmatter":{"layout":"doc","date":"2026-01-02T00:00:00.000Z","author":"俊小赞"},"headers":[],"relativePath":"literature/excerpt-weakness.md","filePath":"literature/excerpt-weakness.md","lastUpdated":1767538251000}'),m={name:"literature/excerpt-weakness.md"},b=l({...m,setup(p){const o=[{content:"给对方留足面子。",date:"2026-01-02"},{content:"如果不是至亲，不要试图改变任何人，顺着他内心的想法去说。教师除外，因为塑造学生是你的工作。",date:"2026-01-02"},{content:"人人都是利己主义者，只不过有人求财，有人求名，有人求安稳。",date:"2026-01-02"},{content:"一个人炫耀什么，说明内心缺少什么。",date:"2026-01-02"},{content:"要想成功，必须具备的条件就是，用你的欲望提升自己的热忱，用你的毅力磨平高山，同时还要相信自己一定会成功。",date:"2026-01-02"},{content:"恐惧来源于对未来的无知。",date:"2026-01-02"},{content:"一个成大事的人，不能处处计较别人，消耗自己的时间去和别人争论，无谓的争论，对自己性情上不但有所损害，而且会失去自己的自制力。",date:"2026-01-02"},{content:"人总是不会接受别人的批评，总是认为自己永远是对的，喜欢找各种各样的借口为自己辩解。",date:"2026-01-02"},{content:"首先想到他人的需求。能够满足他人的需求，这是成功社交的关键。",date:"2026-01-02"},{content:"成功的起点“三不”，不批评、不责备、不抱怨。",date:"2026-01-02"},{content:"男人好色、女人爱美、老人怕死!",date:"2026-01-02"},{content:"你所认为的并非真正的你；反倒是你怎么想，你就是什么样的人。",date:"2026-01-02"},{content:"只要将一个人内心的态度由恐惧转为奋斗，就能克服任何障碍。",date:"2026-01-02"},{content:"所有的人，不管他是屠夫，烤面包的，或者是宝座上的国王，都喜欢尊敬他的人。",date:"2026-01-02"},{content:"如果你过于直率地指出别人的错误，再好的意见也不会被别人接受，甚至会受到很大的伤害。",date:"2026-01-02"},{content:"批评他人是一种自我满足。",date:"2026-01-02"},{content:"成功的人际关系在于你能捕捉对方观点的能力，还有，看一件事须兼顾你和对方的不同角度。",date:"2026-01-02"},{content:"我们滋养了孩子、朋友和雇员的身体，可他们的自尊所需要的滋养，我们所给的却少的可怜。",date:"2026-01-02"},{content:"别老想着自己的那些成就和需要，而是去琢磨琢磨别人的优点，然后把谄媚忘掉，给予他人由衷而诚恳的称赞。",date:"2026-01-02"},{content:"假如我们想交朋友，那就让我们先站出来替他人做些事——做那些需要时间、精力、舍己、体恤的事。",date:"2026-01-02"},{content:"世界上的每个人都在追求快乐——找到快乐的稳妥方法只有一种，那就是通过控制你的思想来实现。快乐不取决于外部条件，而取决于内部条件。",date:"2026-01-02"},{content:"赤裸裸的真相就是，你遇到的所有人差不多都觉得自己在某一方面比你优秀。深入他们内心的稳妥方法就是，巧妙地让他们觉得你认同他们的重要性，而且是真心认同。",date:"2026-01-02"},{content:"每个人都喜欢受人恭维。",date:"2026-01-02"},{content:"我们所相处的对象，并不是绝对理性的动物，而是充满了情绪变化、成见、自负和虚荣的东西。善解人意和宽恕他人，需要有修养自制的功夫。",date:"2026-01-02"},{content:"批评不但不会改变事实，反而会招致愤恨。",date:"2026-01-02"},{content:"成功的秘诀是：「我不说任何人的不好！而说我所知道的每一个人的好处！」。",date:"2026-01-02"},{content:"如果有一个成功秘诀的话，那就是如何得到对方立场的能力；由他的观点设想，正同由你自己的观点一样。",date:"2026-01-02"},{content:"如果你要树立敌人，就胜过你的朋友；但是如果你要得到朋友，那就让你的朋友胜过你。",date:"2026-01-02"},{content:"不要害怕误解，也不要在敌人身上浪费一分一秒。",date:"2026-01-02"},{content:"也许你总是对的，也许你总能驳倒别人，但是那又有什么意义呢。无论输赢，你都无法改变他人的想法。",date:"2026-01-02"}];return(u,t)=>{const d=s("PageInfo");return i(),c("div",null,[t[0]||(t[0]=e("h1",{id:"人性的弱点",tabindex:"-1"},[a("人性的弱点 "),e("a",{class:"header-anchor",href:"#人性的弱点","aria-label":'Permalink to "人性的弱点"'},"​")],-1)),n(d,{readingTime:"4",wordCount:"1.4k",publicTime:""}),t[1]||(t[1]=e("h2",{id:"简介",tabindex:"-1"},[a("简介 "),e("a",{class:"header-anchor",href:"#简介","aria-label":'Permalink to "简介"'},"​")],-1)),t[2]||(t[2]=e("p",null,"《人性的弱点》是卡耐基授课时所用教材演变而来的，他写这本书的初衷只是将它作为教材使用，但为了完成它，卡耐基研究了各种心理学专集，浏览了无数杂志文章，亲身访问了包括富兰克林·罗斯福在内的成功人士。他把一些成功人士的宝贵经验编成一个小册子，由学员们去实践、验证、丰富和发展，使这本小册子越来越厚，最后在 1936 年出版。",-1)),t[3]||(t[3]=e("h2",{id:"摘抄",tabindex:"-1"},[a("摘抄 "),e("a",{class:"header-anchor",href:"#摘抄","aria-label":'Permalink to "摘抄"'},"​")],-1)),t[4]||(t[4]=e("p",null,"《人性的弱点》浓缩成 30 句",-1)),n(r,{data:[...o],reverse:!1},null,8,["data"]),t[5]||(t[5]=e("h2",{id:"结束",tabindex:"-1"},[a("结束 "),e("a",{class:"header-anchor",href:"#结束","aria-label":'Permalink to "结束"'},"​")],-1))])}}});export{x as __pageData,b as default};
